/**
 * Converts the breakpoints array containing objects with a label and px prop
 * to a readable format for the postcss-custom-media plugin.
 *
 * @returns object - list of css variables
 * @example {
 *  '--mobile-landscape': '(min-width: 460px)',
 *  '--tablet-portrait': '(min-width: 768px)',
 *  '--tablet-landscape': '(min-width: 1024px)',
 *  '--desktop': '(min-width: 1280px)',
 *  '--desktop-large': '(min-width: 1440px)',
 *  '--desktop-extra-large': '(min-width: 1920px)'
 * }
 *
 */

module.exports = (function() {

    const breakpoints = require('./breakpoints');

    let obj = {};

    for(let i = 0, l = breakpoints.length; i < l; i++) {
        const breakpoint = breakpoints[i];
        const em = breakpoint.px / 16 + 'em';

        obj['--' + breakpoint.label] = '(min-width: ' + em + ')';
    }

    return obj

})();
